function decode(str) {

  var result = [];

  str = str.split('');
  for (var i = 8; i < str.length; i+=9) {
    str.splice(i,0," ");
  }
  str = str.join("");
  str = str.split(' ');
  console.log(str);
  for (var j = 0; j < str.length; j++) {
    result.push(String.fromCharCode(parseInt(str[j], 2)));
  }

 return result.join('');

}

decode('');
